mapper.select:
select count(*)
from bbs
where isdelete = 'N'

	
		and (title like #{title} or content like #{content})
	
	
		and content like #{content}
	
	
		and content like #{title}
	

or title like #{title}
or content like #{content}

select num, title, ownerid, ownername, createdate, isdelete
from bbs
where isdelete = 'N'

	
		and (title like #{title} or content like #{content})
	
	
		and content like #{content}
	
	
		and content like #{title}
	

order by num desc
limit #{start}, #{count}

mapper.update:
update bbs

	, title = #{title}
	, content = #{content}

where num=#{num}
